      expect(combinedPlugins.states).toEqual(['state-one', 'state-two']);
      expect(combinedPlugins.helpers).toEqual(['helper-one', 'helper-two']);
      expect(combinedPlugins.components).toEqual(['component-one', 'component-two']);
